BEGIN TRANSACTION;

DROP TABLE IF EXISTS users, boards, cards, comments, curriculas, curriculum, curricula, course, courses, enrollment CASCADE;
DROP SEQUENCE IF EXISTS seq_user_id, seq_course_id, seq_enrollment_id, seq_curricula_id CASCADE;
DROP TABLE IF EXISTS users, boards, cards, comments, curriculas, curriculum, curricula, course, courses, enrollment CASCADE;
DROP SEQUENCE IF EXISTS seq_user_id, seq_course_id, seq_enrollment_id, seq_curricula_id CASCADE;


CREATE SEQUENCE seq_user_id
  INCREMENT BY 1
  NO MAXVALUE
  NO MINVALUE;

CREATE SEQUENCE seq_course_id
  INCREMENT BY 1
  NO MAXVALUE
  NO MINVALUE;

CREATE SEQUENCE seq_enrollment_id
  INCREMENT BY 1
  NO MAXVALUE
  NO MINVALUE;

CREATE SEQUENCE seq_curricula_id
  INCREMENT BY 1
  NO MAXVALUE
  NO MINVALUE;

CREATE TABLE users (
   user_id int DEFAULT nextval('seq_user_id'::regclass) NULL,
   username varchar(50) UNIQUE NOT NULL,
   password_hash varchar(200) NOT NULL,
   role varchar(50) NOT NULL,
   CONSTRAINT PK_user PRIMARY KEY (user_id)
);

CREATE TABLE courses (
   course_id int DEFAULT nextval('seq_course_id'::regclass) NOT NULL,
   course_name varchar(70) UNIQUE NOT NULL,
   teacher_id int NOT NULL,
   course_level varchar(50) NOT NULL,
   CONSTRAINT PK_course PRIMARY KEY (course_id)
);

CREATE TABLE enrollment (
   enrollment_id int DEFAULT nextval('seq_enrollment_id'::regclass) NOT NULL,
   student_id int NOT NULL,
   course_id int NOT NULL,
   final_grade int,
   CONSTRAINT PK_enrollment PRIMARY KEY (enrollment_id)
);

CREATE TABLE curriculas (
   curricula_id int DEFAULT nextval('seq_curricula_id'::regclass) NOT NULL,
   course_id int NOT NULL,
   curricula_name varchar(70) UNIQUE NOT NULL,
   summary varchar(1000) NOT NULL,
   description varchar(10000) NOT NULL,
   CONSTRAINT PK_curricula PRIMARY KEY (curricula_id)
);

